// this is where you define the legend for 
// the datasets
var title = "Displaying last 5 movements";
var point_xy = "point xy";

// variables to be used to 
var i, x, y, r = 10, prev_x, prev_y;

// for dataset #1
// this code generates between 3 and 25 random
// x/y points each between -100 and +100
// and a random size between 1 and 2
var data_xy = '';

prev_x = msg.payload.prev_x;
prev_y = msg.payload.prev_y;

if (prev_x === undefined){
    prev_x = 0;
    prev_y = 0;
}

x = getRndInteger(-5,5);
x += prev_x;
y = getRndInteger(-5,5);
y += prev_y;

data_xy += "{x: " + x + ", y: " + y + ", r: " + r + "},";

if (msg.payload.data1 !== undefined){
    data_xy = msg.payload.data1 + "," + data_xy
}

// this line removes the last comma to end the dataset
data_xy = data_xy.replace(/^(.*),(.*?)$/, '$1')

if (data_xy.length > 100) {
    data_xy = data_xy.split("},");
    data_xy.shift();
    data_xy = data_xy.join("},")
}

var length = data_xy.length
// Now we build msg.payload
msg.payload = { "title"    : title,
                "legend1"  : point_xy,
                "data1"    : data_xy,
                "prev_x"   : x,
                "prev_y"   : y,
                "length"   : length};
return msg;

// this code gets a random interger
function getRndInteger(min, max) {
    return Math.floor(Math.random() * (max - min + 1) ) + min;
}
